import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';
import Snap from 'snapsvg-cjs';
import * as d3 from "d3";
import {TweenMax, Power2, TimelineMax, Back} from "gsap/TweenMax";
import './index.css';
import registerServiceWorker from './registerSerivceWorker';

window.onload = function () {
let s = window.Snap;
var letterSpeed = 5; //higher = faster
var colorSpeed = 1; //higher = faster
let colors = [
  '#e9edef', '#efad42', '#e9435a', '#4aadad', '#83cead'
];
let letters = d3.selectAll('.letter');
let tl = new TimelineMax({
  repeat: 1,
  yoyo: true
});

for (var i = 0; i < letters.length; i += 1) {
  var letter = letters[i];

  tl.set(letter.node, {
    rotation: 180 - (Math.random() * 360),
    x: 20,
    y: -200
  }, 0);

  var tw = TweenMax.to(letter.node, 0.6, {
    rotation: 0,
    x: 0,
    y: 0,
    ease: Back.easeOut
  });

  tl.add(tw, 0.5 + (i / letterSpeed));

  var paths = letter.selectAll('*');
