    TweenMax.set(paths[k].node, {
      x: -3,
      y: -3
    });
    var tw = TweenMax.fromTo(paths[k].node, 1, {
      drawSVG: '0%'
    }, {
      drawSVG: '100%',
      ease: Power2.easeInOut
    });
    tl.add(tw, delay);

  }
}

ReactDOM.render(<App />, document.getElementById('root'));
