import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';
import Snap from 'snapsvg-cjs';
import * as d3 from "d3";
import {TweenMax, Power2, TimelineMax, Back} from "gsap/TweenMax";
import './index.css';
import registerServiceWorker from './registerSerivceWorker';


let s = Snap('#surface');
var letterSpeed = 5; //higher = faster
var colorSpeed = 1; //higher = faster
let colors = [
'#e9edef', '#efad42', '#e9435a', '#4aadad', '#83cead'];

let letters = s.selectAll('.letter');
let tl = new TimelineMax({
  repeat: -1,
  yoyo: true });


for (var i = 0; i < letters.length; i += 1) {if (window.CP.shouldStopExecution(0)) break;
  var letter = letters[i];

  tl.set(letter.node, {
    rotation: 180 - Math.random() * 360,
    x: 20,
    y: -200 },
  0);

  var tw = TweenMax.to(letter.node, 0.6, {
    rotation: 0,
    x: 0,
    y: 0,
    ease: Back.easeOut });


  tl.add(tw, 0.5 + i / letterSpeed);

  var paths = letter.selectAll('*');
  for (var k = 0; k < paths.length; k += 1) {if (window.CP.shouldStopExecution(1)) break;
    for (var j = 0; j < colors.length; j += 1) {if (window.CP.shouldStopExecution(2)) break;

      var l = paths[k].clone();
      l.attr({
        stroke: colors[j] });


      var delay = 0.1 + i / letterSpeed + (colors.length - j / colorSpeed) / 10;
      var tw = TweenMax.fromTo(l.node, 1, {
        drawSVG: '0%' },
      {
        drawSVG: '100%',
        ease: Power2.easeInOut });

      tl.add(tw, delay);

    }

    //paths[k].remove();
    window.CP.exitedLoop(2);letter.prepend(paths[k]);
    TweenMax.set(paths[k].node, {
      x: -3,
      y: -3 });

    var tw = TweenMax.fromTo(paths[k].node, 1, {
      drawSVG: '0%' },
    {
      drawSVG: '100%',
      ease: Power2.easeInOut });

    tl.add(tw, delay);

  }window.CP.exitedLoop(1);

}window.CP.exitedLoop(0);


ReactDOM.render(<App />, document.getElementById('root'));
registerServiceWorker();
